/**
 * Random meme from prodota
 */
bot.hears(/–ø–¥/i, (ctx) => {
  console.log("–ø–¥");
  try {
    const scrape = function (callback) {
      let page = Math.floor(Math.random() * 433);
      const options = {
        method: "GET",
        url: `https://prodota.ru/forum/topic/219717/page/${page}/`,
      };
      cloudscraper(options).then((html) => {
        let $ = cheerio.load(html);
        const links = $(".ipsType_normal p");
        let pos = 0;
        $(links).each(function () {
          const pdMemeUrl = $(this).find("img").attr("data-src");
          if (
            pdMemeUrl !== "" &&
            pdMemeUrl !== undefined &&
            !pdMemeUrl.match(/prodota/gi) &&
            !pdMemeUrl.match(/radikal/gi)
          ) {
            pdMemes[pos] = pdMemeUrl;
            console.log(pos + ": " + pdMemeUrl);
            pos++;
          }
        });
        if (callback) callback();
      });
    };

    scrape(function () {
      randomComment();
    });

    const randomComment = function () {
      const x = Math.floor(Math.random() * pdMemes.length);
      console.log("x = " + x);
      ctx.replyWithPhoto({ url: pdMemes[x] });
      pdMemes.length = 0;
    };
  } catch (e) {
    console.error(e);
    ctx.reply("–ß—Ç–æ-—Ç–æ —Å–ª–æ–º–∞–ª–æ—Å—å");
  }
});

